import * as React from 'react';
import { DataGrid } from '@mui/x-data-grid';
import GroupApi from "../../../Api/GroupApi"
// import getListGroupAction from "../../../redux/actions/GroupActions"
import { Button } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import AddCircleOutlineIcon from '@mui/icons-material/AddCircleOutline';
import "./Group.scss"

const columns = [
  { field: 'id', headerName: 'ID', width: 70 },
  { field: 'name', headerName: ' Name', width: 200 },
  {
    field: 'totalMember',
    headerName: 'TotalMember',
    type: 'number',
    width : 300
  },
 
 
];

export default function DataTable(props) {

  // get All Group
  const getListGroup = props.getListGroupAction;
  // const size = props.size;
  React.useEffect(() => {
    const getAllGroup = async () => {
      const result = await GroupApi.getAll();
      const groups = result.data.content;
      // console.log(result);
      setState(groups);
    };
    getAllGroup();
  }, [getListGroup]);

  const [state , setState] = React.useState([]);



  

  // const handleCreate = () =>{
  //   setIsCreating(true)
  // }
  // const handleTable = async () =>{
  //   const res = await GroupApi.getAll()
  //   const group = res.data.content;
  //   console.log(group);
  //   const rows = [
  //     {
  //       id : group.id,
  //       name : group.name,
  //       totalMember : group.totalMember
  //     }
  //   ]
  //   console.log(rows);
  //   setState(group)
  // }
  // const [isOpenModals, setOpenModal] = React.useState(false);

  // Create Group
  const [showTable , setShowTable] = React.useState(false);

  const [groupName, setGroupName] = React.useState("")

  const handleCreateGroup = async  () => {
    try {
    
      const group = await GroupApi.create();
      setGroupName(group.data.groupName);
      setShowTable(true)
    } catch (error) {
      console.log(error);
    }
   
    // const data = new FormData(event.currentTarget);
    // const user = {
    //   name: data.get("name"),
    // }

    // const group =await GroupApi.create (
    //   user.name,
    // )
    // console.log(group);
    // setGroupInfo(group)
    // setOpenModal(false)
    
  };
  //  const deleteGroup =  async ( event)=>{
    
  //  } 
  // const [groupInfo , setGroupInfo] = React.useState()
  // const handleChange  = (event) => {
  //   event.preventDefault();
  //   // Gửi thông tin group đến API hoặc thực hiện các xử lý khác
  //   console.log(groupInfo);
  //   // Reset form
  //   setGroupInfo({
  //     name: ''
  //   });
  //   setIsCreating(false);
  // };
  
  return (
    <div style={{ height: 400, width: '100%' }}>
        <input
        type="text"
        value={groupName}
        // onChange={handleGroupNameChange}
        placeholder="Group Name"
      />
      <button onClick={handleCreateGroup}><AddCircleOutlineIcon fontSize='large'/></button>
       {/* <Button onChange={handleSubmit} ><AddCircleOutlineIcon fontSize='large'/></Button>
       <Button onClick={deleteGroup}><DeleteIcon fontSize='large'/></Button> */}
      <DataGrid
       rows={state}
        columns={columns}
        initialState={{
          pagination: {
            paginationModel: { page: 0, pageSize: 5 },
          },
        }}
        pageSizeOptions={[5, 10]}
        checkboxSelection
      />
     
     
      {/* <Button onClick={handleTable}>onClick</Button> */}
    </div>
  );
}
