import * as React from 'react';
import { styled, alpha } from '@mui/material/styles';
import AppBar from '@mui/material/AppBar';
import Box from '@mui/material/Box';
import Toolbar from '@mui/material/Toolbar';
import IconButton from '@mui/material/IconButton';
import Typography from '@mui/material/Typography';
import InputBase from '@mui/material/InputBase';
import Badge from '@mui/material/Badge';
import MenuItem from '@mui/material/MenuItem';
import Menu from '@mui/material/Menu';
import SearchIcon from '@mui/icons-material/Search';
import AccountCircle from '@mui/icons-material/AccountCircle';
import MailIcon from '@mui/icons-material/Mail';
import NotificationsIcon from '@mui/icons-material/Notifications';
import MoreIcon from '@mui/icons-material/MoreVert';
import "./features.scss"
import { Outlet } from 'react-router-dom';
import ArrowDropDownIcon from '@mui/icons-material/ArrowDropDown';
import ExpandLessIcon from '@mui/icons-material/ExpandLess';
import Promotion from './Header/Promotion';
import News from './Header/News';
import Transport from './Header/Transport';
import Travel from './Header/Travel';


const Search = styled('div')(({ theme }) => ({
  position: 'relative',
  borderRadius: theme.shape.borderRadius,
  backgroundColor: alpha(theme.palette.common.white, 0.15),
  '&:hover': {
    backgroundColor: alpha(theme.palette.common.white, 0.25),
  },
  marginRight: theme.spacing(2),
  marginLeft: 0,
  width: '100%',
  [theme.breakpoints.up('sm')]: {
    marginLeft: theme.spacing(3),
    width: 'auto',
  },
}));

const SearchIconWrapper = styled('div')(({ theme }) => ({
  padding: theme.spacing(0, 2),
  height: '100%',
  position: 'absolute',
  pointerEvents: 'none',
  display: 'flex',
  alignItems: 'center',
  justifyContent: 'center',
}));

const StyledInputBase = styled(InputBase)(({ theme }) => ({
  color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('md')]: {
      width: '20ch',
    },
  },
}));

export default function PrimarySearchAppBar() {
  const [anchorEl, setAnchorEl] = React.useState(null);
  const [mobileMoreAnchorEl, setMobileMoreAnchorEl] = React.useState(null);

  const isMenuOpen = Boolean(anchorEl);
  const isMobileMenuOpen = Boolean(mobileMoreAnchorEl);

  const handleProfileMenuOpen = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleMobileMenuClose = () => {
    setMobileMoreAnchorEl(null);
  };

  const handleMenuClose = () => {
    setAnchorEl(null);
    handleMobileMenuClose();
  };

  const handleMobileMenuOpen = (event) => {
    setMobileMoreAnchorEl(event.currentTarget);
  };
  const menuId = 'primary-search-account-menu';
  const renderMenu = (
    <Menu
      anchorEl={anchorEl}
      anchorOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      id={menuId}
      keepMounted
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      open={isMenuOpen}
      onClose={handleMenuClose}
    >
      <MenuItem onClick={handleMenuClose}>Profile</MenuItem>
      <MenuItem onClick={handleMenuClose}>My account</MenuItem>
    </Menu>
  );

  const mobileMenuId = 'primary-search-account-menu-mobile';
  const renderMobileMenu = (
    <Menu
      anchorEl={mobileMoreAnchorEl}
      anchorOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      id={mobileMenuId}
      keepMounted
      transformOrigin={{
        vertical: 'top',
        horizontal: 'right',
      }}
      open={isMobileMenuOpen}
      onClose={handleMobileMenuClose}
    >
      <MenuItem>
        <IconButton size="large" aria-label="show 4 new mails" color="inherit">
          <Badge badgeContent={4} color="error">
            <MailIcon />
          </Badge>
        </IconButton>
        <p>Messages</p>
      </MenuItem>
      <MenuItem>
        <IconButton
          size="large"
          aria-label="show 17 new notifications"
          color="inherit"
        >
          <Badge badgeContent={17} color="error">
            <NotificationsIcon />
          </Badge>
        </IconButton>
        <p>Notifications</p>
      </MenuItem>
      <MenuItem onClick={handleProfileMenuOpen}>
        <IconButton
          size="large"
          aria-label="account of current user"
          aria-controls="primary-search-account-menu"
          aria-haspopup="true"
          color="inherit"
        >
          <AccountCircle />
        </IconButton>
        <p>Profile</p>
      </MenuItem>
    </Menu>
  );
 
 // Hiện menu khi click vào Du lịch ở header
    const [isOpenMenu, setIsMenuOpen] = React.useState(false);
  
    const toggleMenu = () => {
      setIsMenuOpen(!isMenuOpen);
    };

    const closeMenu = () => {
      setIsMenuOpen(false);
    };


    // Vận chuyển
    const [isOpenMenuTransport, setIsMenuOpenTransport] = React.useState(false);
  
    const toggleMenuTransport = () => {
      setIsMenuOpenTransport(!isOpenMenuTransport);
    };

    const closeMenuTranssport = () => {
      setIsMenuOpenTransport(false);
    };

    // Tin tức

    const [isOpenMenuNews, setIsMenuOpenNews] = React.useState(false);
  
    const toggleMenuNews = () => {
      setIsMenuOpenNews(!isOpenMenuNews);
    };

    const closeMenuNews = () => {
      setIsMenuOpenNews(false);
    };

    // Khuyến mãi
    const [isOpenMenuPromotion, setIsMenuOpenPromotion] = React.useState(false);
  
    const toggleMenuPromotion = () => {
      setIsMenuOpenPromotion(!isOpenMenuPromotion);
    };

    const closeMenuPromotion = () => {
      setIsMenuOpenPromotion(false);
    };


  return (
    <Box sx={{ flexGrow: 12 }}>
      <AppBar  position="static" color="">
      <Toolbar >
      <Typography
            variant="h6"
            noWrap
            component="div"
            sx={{ display: { xs: 'none', sm: 'block' } }}
          >
            <img style={{height : "70px" ,width : "100px"}} src='https://www.creativefabrica.com/wp-content/uploads/2021/03/20/Travel-logo-design-Graphics-9786083-1-1-580x435.jpg'></img>
          </Typography>

          {/* <Box sx={{ flexGrow: 1 }} /> */}
          <Box
            sx={{
              display: { xs: 'none', md: 'flex' },
            }}
          >
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px",cursor: 'pointer', position: 'relative'}} 
              onClick={isOpenMenu ? closeMenu : toggleMenu}
            >
              Du lịch
                {isOpenMenu ? (
                  <ExpandLessIcon style={{ marginLeft: '5px' }} />
                    ) : (
                      <ArrowDropDownIcon style={{ marginLeft: '5px' }} />
                    )}
            </Typography>
            {isOpenMenu && <div className="slide-in-travel">
          <Travel />
        </div>}
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px"}} 
            >
              <a className='a' href='https://vietravelmice.com/'> Vietravel MICE</a>
             
            </Typography>
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px",cursor: 'pointer', position: 'relative'}} 
              onClick={!isOpenMenu && isOpenMenuTransport ? closeMenuTranssport : toggleMenuTransport}
            >
              Vận Chuyển
              {isOpenMenuTransport ? (
                  <ExpandLessIcon style={{ marginLeft: '5px' }} />
                    ) : (
                      <ArrowDropDownIcon style={{ marginLeft: '5px' }} />
                    )}
            </Typography>
            {isOpenMenuTransport && <div className="slide-in-transport">
          <Transport />
        </div>}
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px",cursor: 'pointer', position: 'relative'}} 
              onClick={isOpenMenuNews ? closeMenuNews : toggleMenuNews}
            >
             Tin Tức
             {isOpenMenuNews ? (
                  <ExpandLessIcon style={{ marginLeft: '5px' }} />
                    ) : (
                      <ArrowDropDownIcon style={{ marginLeft: '5px' }} />
                    )}
             
            </Typography>
            {isOpenMenuNews && <div className="slide-in-news">
          <News />
        </div>}
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px" ,cursor: 'pointer', position: 'relative'}} 
              onClick={isOpenMenuPromotion ? closeMenuPromotion : toggleMenuPromotion}
            >
              Khuyến Mãi
              {isOpenMenuPromotion ? (
                  <ExpandLessIcon style={{ marginLeft: '5px' }} />
                    ) : (
                      <ArrowDropDownIcon style={{ marginLeft: '5px' }} />
                    )}
            </Typography>
            {isOpenMenuPromotion && <div className="slide-in-promotion">
          <Promotion />
        </div>}
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px"}} 
            >
              <a className="a" href='https://vietravelplus.com/'> VietravelPlus</a>
             
            </Typography>
            <Typography
              variant="h6"
              noWrap
              component="div"
              sx={{ display: { xs: 'none', sm: 'block' } }}
              style={{padding:"20px" , left:"20px"}} 
            >
              <a className='a' href='https://travel.com.vn/lien-he.aspx'> Liên Hệ</a>
            </Typography>
          </Box>

          <Box sx={{ flexGrow: 1 }} />
          <Box sx={{ display: { xs: 'none', md: 'flex' } }}>
            <Search>
              <SearchIconWrapper>
                <SearchIcon />
              </SearchIconWrapper>
              <StyledInputBase
                placeholder="Bắt đầu tìm kiếm"
                inputProps={{ 'aria-label': 'search' }}
              />
            </Search>
          
            <IconButton
              size="large"
              edge="end"
              aria-label="account of current user"
              aria-controls={menuId}
              aria-haspopup="true"
              onClick={handleProfileMenuOpen}
              color="inherit"
            >
              <AccountCircle />
            </IconButton>
          </Box>
          <Box sx={{ display: { xs: 'flex', md: 'none' } }}>
            <IconButton
              size="large"
              aria-label="show more"
              aria-controls={mobileMenuId}
              aria-haspopup="true"
              onClick={handleMobileMenuOpen}
              color="inherit"
            >
              <MoreIcon />
            </IconButton>
          </Box>
          
        </Toolbar>
       
      </AppBar>
      <Outlet/>
      {renderMobileMenu}
      {renderMenu}
    </Box>
  );
}
