import React, { useState } from 'react'

export default function Travel() {

  const [menuStates, setMenuStates] = useState({});

  const toggleMenu = (menuId) => {
    setMenuStates((prevMenuStates) => ({
      ...prevMenuStates,
      [menuId]: !prevMenuStates[menuId],
    }));
  };

  return (
    <div>
      <div className="style" onClick={() => toggleMenu('menu1')}>
        <h3 className="li">Du lịch trong nước</h3>
      </div>

      {menuStates['menu1'] && (
        <div className="menu">
          <ul>
            <li>Menu item 1</li>
            <li>Menu item 2</li>
            <li>Menu item 3</li>
          </ul>
        </div>
      )}

      <div className="style" onClick={() => toggleMenu('menu2')}>
        <h3 className="li">Du lịch nước ngoài</h3>
      </div>

      {menuStates['menu2'] && (
        <div className="menu">
          <ul>
            <li>Menu item A</li>
            <li>Menu item B</li>
            <li>Menu item C</li>
          </ul>
        </div>
      )}
    </div>
  )
}
