import React, { useState, useEffect } from 'react';
import "./Endow.scss"

const images = [
  'https://media.travel.com.vn/Advertisings/bn_230714_BannerWeb-TOURCHATGIATOT_My.webp',
  'https://media.travel.com.vn/Advertisings/bn_230721_BANNER%20WEB_HA%20TIEN_412X309.webp',
  'https://media.travel.com.vn/Advertisings/bn_230714_BannerWeb-TOURCHATGIATOT_PhanThiet.webp',
];

const Slide = () => {
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
  
    useEffect(() => {
      const timer = setInterval(() => {
        setCurrentImageIndex((prevIndex) =>
          prevIndex === images.length - 1 ? 0 : prevIndex + 1
        );
      }, 3000); // Thời gian chuyển đổi giữa các hình ảnh (3 giây)
  
      return () => clearInterval(timer);
    }, []);
  
    return (
      <div className="slide">
        <div className="images-container" style={{ transform: `translateX(-${currentImageIndex * 100}%)` }}>
          {images.map((imageUrl, index) => (
            <img
              key={index}
              src={imageUrl}
              alt={`Slide ${index}`}
              className={`image ${index === currentImageIndex ? 'active' : ''}`}
            />
          ))}
        </div>
      </div>
    );
  };
  
  export default Slide;
